import axios from 'axios';

const routerUrl = "http://1001guide.net:8000/server/api/";
const siteUrl = "http://1001guide.net:8000/server";
const uploadDir = siteUrl + "/upload/";

/**
 * Send request to server
 *
 * @param {object} dataToSend
 * @param {string} url
 * @return {AxiosPromise<any>}
 */
const sendRequest = (url, method='get', dataToSend = null) => {
  let data = new FormData();
  if ( dataToSend ) {
    Object.keys(dataToSend).forEach(function (key) {
      data.set(key, dataToSend[key]);
    });
  }
  return axios[method](
    routerUrl+url,
    data
  )
};


export {routerUrl, siteUrl, uploadDir, sendRequest};
